CREATE TABLE XLS.COLUMNS (
    ID NUMBER
    , TABLE_SCHEMA VARCHAR2(30) NOT NULL
    , TABLE_NAME VARCHAR2(30) NOT NULL
    , COLUMN_NAME VARCHAR2(30) NOT NULL
    , ORDINAL_POSITION NUMBER NOT NULL
    , IS_PRIMARY_KEY NUMBER(1,0)
    , IS_NULLABLE NUMBER(1,0)
    , IS_IDENTITY NUMBER(1,0)
    , IS_COMPUTED NUMBER(1,0)
    , COLUMN_DEFAULT VARCHAR2(255)
    , DATA_TYPE VARCHAR2(30)
    , CHARACTER_MAXIMUM_LENGTH NUMBER
    , PRECISION NUMBER
    , SCALE NUMBER
    , CONSTRAINT PK_COLUMNS PRIMARY KEY (ID)
);

CREATE UNIQUE INDEX XLS.IX_COLUMNS ON XLS.COLUMNS (TABLE_SCHEMA, TABLE_NAME, COLUMN_NAME);

CREATE SEQUENCE XLS.SQ_COLUMNS_ID;

CREATE TRIGGER XLS.TR_COLUMNS_ID
    BEFORE INSERT OR UPDATE
    ON XLS.COLUMNS
    REFERENCING NEW AS NEW OLD AS OLD FOR EACH ROW
BEGIN
    IF :NEW.ID IS NULL THEN
        SELECT SQ_COLUMNS_ID.NEXTVAL INTO :NEW.ID FROM DUAL;
    END IF;
END;
/

