CREATE OR REPLACE VIEW XLS.QUERIES (
    TABLE_SCHEMA,
    TABLE_NAME,
    TABLE_TYPE,
    TABLE_CODE,
    INSERT_PROCEDURE,
    UPDATE_PROCEDURE,
    DELETE_PROCEDURE,
    PROCEDURE_TYPE
)
AS
SELECT
    t.OWNER AS TABLE_SCHEMA
    , t.OBJECT_NAME AS TABLE_NAME
    , t.OBJECT_TYPE AS TABLE_TYPE
    , NULL AS TABLE_CODE
    , NULL AS INSERT_PROCEDURE
    , NULL AS UPDATE_PROCEDURE
    , NULL AS DELETE_PROCEDURE
    , NULL AS PROCEDURE_TYPE
FROM
    SYS.ALL_OBJECTS t
WHERE
    t.OWNER NOT IN (
        'ANONYMOUS','APPQOSSYS','AUDSYS','CTXSYS','DBSFWUSER','DBSNMP','DIP','DVF','DVSYS','GGSYS',
        'GSMADMIN_INTERNAL','GSMCATUSER','GSMUSER','LBACSYS','MDDATA','MDSYS','OJVMSYS','OLAPSYS',
        'ORACLE_OCM','ORDDATA','ORDPLUGINS','ORDSYS','OUTLN','REMOTE_SCHEDULER_AGENT',
        'SI_INFORMTN_SCHEMA','SYS','SYS$UMF','SYSBACKUP','SYSDG','SYSKM','SYSRAC','SYSTEM','WMSYS','XDB','XS$NULL',
        'APEX_030200','EXFSYS','FLOWS_FILES','IX','OWBSYS','SYSMAN',
        'XLS', 'SAVETODB_DEV', 'SAVETODB_XLS'
        )
    AND t.OBJECT_TYPE IN ('TABLE', 'VIEW', 'PROCEDURE')
    AND t.OBJECT_NAME NOT LIKE '%_INSERT'
    AND t.OBJECT_NAME NOT LIKE '%_UPDATE'
    AND t.OBJECT_NAME NOT LIKE '%_DELETE'
    AND t.OBJECT_NAME NOT LIKE '%_MERGE'
    AND t.OBJECT_NAME NOT LIKE '%_CHANGE'
    AND t.OBJECT_NAME NOT LIKE 'XL_%'
UNION ALL
SELECT
    o.TABLE_SCHEMA
    , o.TABLE_NAME
    , o.TABLE_TYPE
    , o.TABLE_CODE
    , o.INSERT_OBJECT AS INSERT_PROCEDURE
    , o.UPDATE_OBJECT AS UPDATE_PROCEDURE
    , o.DELETE_OBJECT AS DELETE_PROCEDURE
    , NULL AS PROCEDURE_TYPE
FROM
    XLS.OBJECTS o
WHERE
    o.TABLE_TYPE IN ('CODE', 'HTTP', 'TEXT')
    AND o.TABLE_SCHEMA IS NOT NULL
    AND o.TABLE_NAME IS NOT NULL
    AND o.TABLE_CODE IS NOT NULL
    AND NOT o.TABLE_NAME LIKE 'XL_%'
;


CREATE OR REPLACE VIEW XLS.USERS (
    "USER"
    , "ROLE"
)
AS
SELECT
    u.USERNAME AS "USER"
    , r.GRANTED_ROLE AS "ROLE"
FROM
    SYS.DBA_USERS u
    LEFT OUTER JOIN SYS.DBA_ROLE_PRIVS r ON r.GRANTEE = u.USERNAME AND r.GRANTED_ROLE LIKE 'XLS_%'
WHERE
    u.ACCOUNT_STATUS = 'OPEN'
    AND NOT u.DEFAULT_TABLESPACE IN ('SYSTEM')
    AND u.LAST_LOGIN IS NOT NULL
    AND u.USERNAME NOT IN ('SYS', 'SYSTEM')
UNION ALL
SELECT
    NULL AS "USER"
    , r.ROLE AS "ROLE"
FROM
    SYS.DBA_ROLES r
WHERE
    r.ROLE LIKE 'XLS_%';

