CREATE TABLE XLS.COLUMNS (
    ID NUMBER
    , TABLE_SCHEMA VARCHAR2(30) NOT NULL
    , TABLE_NAME VARCHAR2(30) NOT NULL
    , COLUMN_NAME VARCHAR2(30) NOT NULL
    , ORDINAL_POSITION NUMBER NOT NULL
    , IS_PRIMARY_KEY NUMBER(1,0)
    , IS_NULLABLE NUMBER(1,0)
    , IS_IDENTITY NUMBER(1,0)
