    , DATA_TYPE VARCHAR2(30)
    , CHARACTER_MAXIMUM_LENGTH NUMBER
    , PRECISION NUMBER
    , SCALE NUMBER
    , CONSTRAINT PK_COLUMNS PRIMARY KEY (ID)
);

