CREATE SEQUENCE XLS.SQ_COLUMNS_ID;

CREATE TRIGGER XLS.TR_COLUMNS_ID
    BEFORE INSERT OR UPDATE
    ON XLS.COLUMNS
    REFERENCING NEW AS NEW OLD AS OLD FOR EACH ROW
BEGIN
    IF :NEW.ID IS NULL THEN
        SELECT SQ_COLUMNS_ID.NEXTVAL INTO :NEW.ID FROM DUAL;
    END IF;
END;
